import React, { useState,useEffect } from 'react';

const Goals = () => {
  const [goal, setGoal] = useState('');
  const [newGoal,setNewGoal]=useState('');

  const handleGoalChange = (e) => {
    setGoal(e.target.value);
  };

  useEffect(() => {
    // Fetch goals from the backend when the component mounts
    fetchGoals();
  }, []); // Empty dependency array ensures this effect runs only once

  const fetchGoals = async () => {
    try {
      const response = await fetch('/api/goals');
      if (response.ok) {
        const data = await response.json();
        setGoal(data);
      } else {
        console.error('Failed to fetch goals');
      }
    } catch (error) {
      console.error('Error fetching goals:', error);
    }
  };


  const saveGoal = async () => {
    try {
      // Make a POST request to add a new goal
      const response = await fetch('/api/goals', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ text: newGoal }),
      });

      if (response.ok) {
        console.log('Goal added successfully');
        // Fetch goals again after adding a new goal
        fetchGoals();
        // Optionally, you can reset the form or update the UI as needed
        setNewGoal('');
      } else {
        console.error('Failed to add goal');
      }
    } catch (error) {
      console.error('Error adding goal:', error);
    }
    console.log('Saving Goal:', goal);
  };

  return (
    <div className='Goals'>
      <h2>Goals and Targets</h2>
      <input
        type="text"
        value={goal}
        onChange={handleGoalChange}
        placeholder="Set your goal..."
      />
      <br />
      <button onClick={saveGoal}>Set Goal</button>
    </div>
  );
};

export default Goals;
