import {useNavigate} from 'react-router-dom'
import { useState } from 'react';

const EditGoal = ({goal}) => {

    const handleUpdateGoal = async () => {

        const [newGoal,setNewGoal] = useState(goal.text);
        const [targetDate, setTargetDate] = useState(goal.targetDate);
        const navigate = useNavigate()
        
        
        try {
          // Make a POST request to add a new goal
          const response = await fetch(`${URL}/api/goals`, {
            method: 'PUT',
            headers: {
              'Content-Type': 'application/json',
            },
            param:  JSON.stringify({ id: goal.id}),
            body: JSON.stringify({ text: newGoal , targetDate: targetDate }),
          });
    
          if (response.ok) {
            console.log('Goal Updated successfully');
            navigate('/goals')
          } else {
            console.error('Failed to add goal');
          }
        } catch (error) {
          console.error('Error adding goal:', error);
        }
      };

    return (
          
            <form onSubmit={e=>e.preventDefault()}>
           <label> Edit your Goal </label>
            <input
              type="text"
              placeholder="Enter your goal"
              value={goal.text}
              onChange={(e) => setNewGoal(e.target.value)}
            />
            <label> Edit your Goal </label>
             <input
              type="date"
              value={goal.targetDate}
              onChange={(e) => setTargetDate(e.target.value)}
            />
            <button onClick={handleUpdateGoal}>Update Goal</button>
          </form>
        )
        } 
      
    
  
export default EditGoal
