import React from 'react'
import { Link } from 'react-router-dom';
import EditGoal from './EditGoal';

const Goal = ({goal}) => {
    let now = new Date();
    let tDate = new Date(goal.targetDate);
    const dateColor = now>tDate ? 'Red' : 'Green'

  return (
     
        <tr className='goalsRow' key={goal.id} >
          <td>{goal.text} </td>
          <td style={{color: `${dateColor}`}}>{
            new Date(goal.targetDate).toLocaleString('en-US',{
            day:'numeric', month:'long', year:'numeric' })}</td>
            <td> <Link to={<EditGoal goal={goal}/>}>Edit</Link></td>
        </tr>
        
  )
}

export default Goal
