        ? ids.map (noteId => <Goal key={id} />)
        : null

  return (
    <div className='Goals'>
      <h2>Set your Goals and time to track your progress </h2>
      <div>
        <input
          type="text"
          placeholder="Enter your goal"
          value={newGoal}
          onChange={(e) => setNewGoal(e.target.value)}
        />
         <input
          type="date"
          value={targetDate}
          onChange={(e) => setTargetDate(e.target.value)}
        />
        <button onClick={handleAddGoal}>Add Goal</button>
      </div>
      <table className='goalsTable'>
        <thead>
          <tr>
            <th>Goal</th>
            <th>Target</th>
            <th> Edit</th>
          </tr>
        </thead>
        <tbody>
     
        {goals.map((goal) => (
        <tr className='goalsRow' key={goal.id} >
          <td>{goal.text} </td>
          <td>{
            new Date(goal.targetDate).toLocaleString('en-US',{
            day:'numeric', month:'long', year:'numeric' })}</td>
            <td> <a href=''>Edit</a></td>
            </tr>
        ))}
       </tbody>
    </table>
    </div>
  );
};

export default Goals;
