import React from 'react'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPenToSquare } from "@fortawesome/free-solid-svg-icons";
import { faTrash } from '@fortawesome/free-solid-svg-icons';

const Goal = ({goal}) => {
    let now = new Date();
    let tDate = new Date(goal.targetDate);
    const dateColor = now>tDate ? 'Red' : 'Green'

  return (
     
        <tr className='goalsRow' key={goal.id} >
          <td>{goal.text} </td>
          <td style={{color: `${dateColor}`}}>{
            new Date(goal.targetDate).toLocaleString('en-US',{
            day:'numeric', month:'long', year:'numeric' })}</td>
            <td> <button onClick={handleEdit}><FontAwesomeIcon icon={faPenToSquare} /></button></td>
            <td> <button onClick={handleDelete}><FontAwesomeIcon icon={faTrash} /></button></td>
        </tr>
        
  )
}

export default Goal
